function [child1, child2]=crossover(parent1, parent2,mask)tmp=randi([0,2],1,2); % to swap the locations to form two childrenchild1 = parent1;child1( [tmp(1)*4+1:tmp(1)*4+4]) = parent2( [tmp(2)*4+1:tmp(2)*4+4]);child2 = parent2;child2( [tmp(2)*4+1:tmp(2)*4+4]) = parent1( [tmp(1)*4+1:tmp(1)*4+4]);child1(1, 13)=error_fun3( child1(1,[1:12]),mask) ; child2(1, 13)=error_fun3( child2(1,[1:12]),mask) ; 