function [child]=mutation(parent,Y_min,Y_max,X_min,X_max,AA,BB,mask)  tmp=randi([1,12]); % to swap the locations to form two children  dir = randi([1,2]);  child=parent;if (dir ==1)  % the case for increasing the value by 1switch tmpcase {1,5,9}  % ('Y')  child (tmp) = child(tmp)+1 ;  if   (child (tmp) > Y_max)         child(tmp) = Y_max;  endcase {2,6,10}  % ('X')   child (tmp) = child(tmp)+1 ;  if   (child (tmp) > X_max)         child(tmp) = X_max;  endotherwise % ('Size')   child (tmp) = child(tmp)+1 ;  if   (child (tmp) > BB)         child(tmp) = BB;  endendend % of dir == 1if (dir ==2)  % the case for decreasing the value by 1switch tmpcase {1,5,9}  % ('Y')  child (tmp) = child(tmp)-1 ;  if   (child (tmp) < Y_min)         child(tmp) = Y_min;  endcase {2,6,10}  % ('X')   child (tmp) = child(tmp)-1 ;  if   (child (tmp) > X_min)         child(tmp) = X_min;  endotherwise % ('Size')   child (tmp) = child(tmp)-1 ;  if   (child (tmp) < AA)         child(tmp) = AA;  endendend % of dir == 2child(1, 13)=error_fun3( child(1,[1:12]),mask) ; 